mapper.insert:
	   INSERT INTO gBoard(    gNo 
	   					      , gTitle 
	   					      , gWriter
	   					      , gContents 
	   					      , fNo
	   					      )
	                 VALUES(    gBoard_SEQ.NEXTVAL 
	                 		  , #{gTitle}
	                 		  , #{gWriter}
	                 		  , #{gContents}
	                 		  , SEQ_FILEUP_NO.nextval
	                 		    )

mapper.update:
UPDATE gBoard SET
gViews = gViews+1
WHERE gNo = #{gNo}

UPDATE gBoard
 SET gTitle = #{gTitle},
 	 gContents = #{gContents}
 WHERE gNo = #{gNo}
